var 
    Fs   = require( "fs" ),
    Path = require( "path" ),
    BRUSH_ALIASES = {}
;

Fs.readdirSync( "./brushs"  ).forEach( function( brush_file ){
    if( brush_file.substr(-2) !== "js" ){
        return;
    }
    var brush = require( "./brushs/" + brush_file ).Brush;
    brush.aliases.forEach( function( aliase ){
        BRUSH_ALIASES[ aliase ] = brush_file
    } );
} )
console.log( BRUSH_ALIASES );
